#!/bin/bash
    cd "$(dirname "$0")"
    ip=$(hostname -i)
    (geth --datadir node1 --nousb --unlock "0,1" --password node1/node1.password --rpc -rpcapi eth,web3,personal,admin --rpccorsdomain '*' --nat extip:`hostname -i` --maxpendpeers 4 --mine --minerthreads 1 --allow-insecure-unlock --port 30311 --rpcaddr "0.0.0.0" --rpcport 8541 --networkid 191 --miner.etherbase 0x073CFa4b6635b1A1B96F6363a9e499a8076B6107 --syncmode 'full' --ipcdisable &> node1.log &) 
    sleep 3
    full_enode1=$(geth --exec "admin.nodeInfo.enode" attach http://$ip:8541)
    enode1=$(expr substr $full_enode1 1 $(($(expr index $full_enode1 "@") - 1)))'@127.0.0.1:30311"'
    (geth --datadir node2 --nousb --unlock 0 --password node2/node2.password --rpc -rpcapi eth,web3,personal,admin --rpccorsdomain '*' --nat extip:`hostname -i`  --maxpendpeers 4 --mine --minerthreads 1 --allow-insecure-unlock --port 30312 --rpcaddr "0.0.0.0" --rpcport 8542 --networkid 191 --miner.etherbase 0x0Ce59225BCD447fEaEd698ED754D309febA5fC63 --syncmode 'full' --ipcdisable &> node2.log &)
    sleep 3
    $(geth --exec "admin.addPeer($enode1)" attach http://$ip:8542)  
    while ! ( ( grep -q 'number=3' ./node1.log ) && ( grep -q 'number=3' ./node2.log) )
	do
		sleep 2
		echo "Waiting for node..."
	done

   contract="var total = 1200; var erc20_coinContract = web3.eth.contract([{'inputs':[{'internalType':'uint256','name':'total','type':'uint256'}],'payable':false,'stateMutability':'nonpayable','type':'constructor'},{'anonymous':false,'inputs':[{'indexed':true,'internalType':'address','name':'tokenOwner','type':'address'},{'indexed':true,'internalType':'address','name':'spender','type':'address'},{'indexed':false,'internalType':'uint256','name':'tokens','type':'uint256'}],'name':'Approval','type':'event'},{'anonymous':false,'inputs':[{'indexed':true,'internalType':'address','name':'from','type':'address'},{'indexed':true,'internalType':'address','name':'to','type':'address'},{'indexed':false,'internalType':'uint256','name':'tokens','type':'uint256'}],'name':'Transfer','type':'event'},{'constant':true,'inputs':[{'internalType':'address','name':'holder','type':'address'},{'internalType':'address','name':'delegate','type':'address'}],'name':'allowance','outputs':[{'internalType':'uint256','name':'','type':'uint256'}],'payable':false,'stateMutability':'view','type':'function'},{'constant':false,'inputs':[{'internalType':'address','name':'delegate','type':'address'},{'internalType':'uint256','name':'numTokens','type':'uint256'}],'name':'approve','outputs':[{'internalType':'bool','name':'','type':'bool'}],'payable':false,'stateMutability':'nonpayable','type':'function'},{'constant':true,'inputs':[{'internalType':'address','name':'tokenOwner','type':'address'}],'name':'balanceOf','outputs':[{'internalType':'uint256','name':'','type':'uint256'}],'payable':false,'stateMutability':'view','type':'function'},{'constant':true,'inputs':[],'name':'decimals','outputs':[{'internalType':'uint8','name':'','type':'uint8'}],'payable':false,'stateMutability':'view','type':'function'},{'constant':true,'inputs':[],'name':'name','outputs':[{'internalType':'string','name':'','type':'string'}],'payable':false,'stateMutability':'view','type':'function'},{'constant':true,'inputs':[],'name':'owner','outputs':[{'internalType':'address','name':'','type':'address'}],'payable':false,'stateMutability':'view','type':'function'},{'constant':true,'inputs':[],'name':'symbol','outputs':[{'internalType':'string','name':'','type':'string'}],'payable':false,'stateMutability':'view','type':'function'},{'constant':true,'inputs':[],'name':'totalSupply','outputs':[{'internalType':'uint256','name':'','type':'uint256'}],'payable':false,'stateMutability':'view','type':'function'},{'constant':false,'inputs':[{'internalType':'address','name':'receiver','type':'address'},{'internalType':'uint256','name':'numTokens','type':'uint256'}],'name':'transfer','outputs':[{'internalType':'bool','name':'','type':'bool'}],'payable':false,'stateMutability':'nonpayable','type':'function'},{'constant':false,'inputs':[{'internalType':'address','name':'holder','type':'address'},{'internalType':'address','name':'buyer','type':'address'},{'internalType':'uint256','name':'numTokens','type':'uint256'}],'name':'transferFrom','outputs':[{'internalType':'bool','name':'','type':'bool'}],'payable':false,'stateMutability':'nonpayable','type':'function'}]);var erc20_coin = erc20_coinContract.new(total,{from: web3.eth.accounts[1], data: '', gas: '4700000'}, function (e, contract){console.log(e, contract);if (typeof contract.address !== 'undefined') {console.log('Contract mined! address: ' + contract.address + ' transactionHash: ' + contract.transactionHash);}})"

    
    geth --exec "$contract" attach http://$ip:8541 &> node1.log

    code=$(geth --exec "eth.getCode('0x56bc568b19d37b5742f60ac3f4c56a3b3d266aee')" attach http://$ip:8541)

    while [ "$code" == "0x" ]
	do
		sleep 2
		echo "Waiting for contrract..."
        $code=$(geth --exec "eth.getCode('0x56bc568b19d37b5742f60ac3f4c56a3b3d266aee')" attach http://$ip:8541)
        echo $code
	done

    echo "The node is ready!"
    sleep infinity